import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import './App.css';
import UserDashboard from './pages/UserDashboard';
import AdminDashboard from './pages/AdminDashboard';

// Navigation component to handle view switching
const Navigation = ({ isAdmin, onViewChange }) => {
  const location = useLocation();
  const navigate = useNavigate();

  // Update URL when view changes
  useEffect(() => {
    if (isAdmin && !location.pathname.includes('admin')) {
      navigate('/admin');
    } else if (!isAdmin && !location.pathname.includes('user')) {
      navigate('/user');
    }
  }, [isAdmin, location.pathname, navigate]);

  return (
    <nav className="main-nav">
      <div className="nav-container">
        <h1>ðŸ’» Lab Management System</h1>
        <div className="nav-links">
          <button 
            className={`nav-button ${!isAdmin ? 'active' : ''}`}
            onClick={() => onViewChange(false)}
          >
            <span className="nav-icon">ðŸ‘¤</span>
            <span className="nav-text">User View</span>
          </button>
          <button 
            className={`nav-button ${isAdmin ? 'active' : ''}`}
            onClick={() => onViewChange(true)}
          >
            <span className="nav-icon">ðŸ”§</span>
            <span className="nav-text">Admin View</span>
          </button>
        </div>
      </div>
    </nav>
  );
};

// Main App component
function App() {
  const [isAdmin, setIsAdmin] = useState(false);
  const location = useLocation();

  // Set initial view based on URL
  useEffect(() => {
    const path = location.pathname;
    if (path.includes('admin')) {
      setIsAdmin(true);
    } else {
      setIsAdmin(false);
    }
  }, [location.pathname]);

  const handleViewChange = (adminView) => {
    setIsAdmin(adminView);
  };

  return (
    <div className="app">
      <Navigation isAdmin={isAdmin} onViewChange={handleViewChange} />
      <main className="app-content">
        <Routes>
          <Route path="/" element={<UserDashboard />} />
          <Route path="/user" element={<UserDashboard />} />
          <Route path="/admin" element={<AdminDashboard />} />
        </Routes>
      </main>
      <footer className="app-footer">
        <p>Â© {new Date().getFullYear()} Lab Management System. All rights reserved.</p>
      </footer>
    </div>
  );
}

// Wrapper component to provide router context
function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

export default AppWrapper;
