import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ComputerList from '../components/ComputerList';
import '../styles/Dashboard.css';
import '../styles/AdminDashboard.css';
import '../styles/ComputerList.css';

const AdminDashboard = () => {
  const [labStatus, setLabStatus] = useState({
    totalComputers: 0,
    workingComputers: 0,
    lastUpdated: null
  });
  const [formData, setFormData] = useState({
    totalComputers: '',
    workingComputers: ''
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isUpdating, setIsUpdating] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();
  const username = 'Admin User';

  useEffect(() => {
    fetchLabStatus();

    // Set up auto-refresh every 30 seconds
    const intervalId = setInterval(fetchLabStatus, 30000);

    return () => clearInterval(intervalId);
  }, []);

  const fetchLabStatus = async () => {
    try {
      setIsLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 800));

      // Mock data - in a real app, this would come from an API
      const mockData = {
        totalComputers: 25,
        workingComputers: 22,
        lastUpdated: new Date().toISOString()
      };

      setLabStatus(mockData);
      setFormData({
        totalComputers: mockData.totalComputers.toString(),
        workingComputers: mockData.workingComputers.toString()
      });
    } catch (err) {
      setError('Failed to fetch lab status. Please try again later.');
      console.error('Error fetching lab status:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    // Only allow numbers
    if (value === '' || /^\d+$/.test(value)) {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const total = parseInt(formData.totalComputers, 10);
    const working = parseInt(formData.workingComputers, 10);

    if (isNaN(total) || isNaN(working) || total < 0 || working < 0) {
      setError('Please enter valid numbers');
      return;
    }

    if (working > total) {
      setError('Working computers cannot be more than total computers');
      return;
    }

    try {
      setIsUpdating(true);
      setError('');
      setSuccess('');

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // In a real app, this would be an API call
      // await api.updateLabStatus({ totalComputers: total, workingComputers: working });

      // Update local state
      const updatedStatus = {
        totalComputers: total,
        workingComputers: working,
        lastUpdated: new Date().toISOString()
      };

      setLabStatus(updatedStatus);
      setSuccess('Lab status updated successfully!');

      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to update lab status. Please try again.');
      console.error('Error updating lab status:', err);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleLogout = () => {
    // No logout needed in this version
    navigate('/');
  };

  const handleComputerStatusChange = (computerId, newStatus) => {
    // In a real app, this would update the status via an API
    console.log(`Admin: Computer ${computerId} status changed to: ${newStatus}`);

    // Show a success message
    setSuccess(`Computer ${computerId} marked as ${newStatus.replace('_', ' ')}`);
    setError('');

    // Clear success message after 5 seconds
    setTimeout(() => setSuccess(''), 5000);
  };

  return (
    <div className="dashboard admin-dashboard">
      <div className="dashboard-header">
        <h1>💻 Lab Management System - Admin</h1>
        <div className="user-info">
          <span>Welcome, {username}</span>
          <button onClick={handleLogout} className="logout-button">
            Switch to User View
          </button>
        </div>
      </div>

      <main className="dashboard-content">
        {isLoading ? (
          <div className="loading">Loading lab status...</div>
        ) : error ? (
          <div className="error-message">
            <span className="error-icon">⚠️</span>
            {error}
          </div>
        ) : (
          <>
            <div className="admin-overview">
              <div className="status-summary">
                <div className="status-card total">
                  <div className="card-icon">💻</div>
                  <h3>Total Computers</h3>
                  <div className="status-value">{labStatus.totalComputers}</div>
                  <div className="status-trend">
                    <span className="trend-up">+2 this month</span>
                  </div>
                </div>

                <div className="status-card working">
                  <div className="card-icon">✅</div>
                  <h3>Operational</h3>
                  <div className="status-value">{labStatus.workingComputers}</div>
                  <div className="status-trend">
                    <span className="trend-up">+3 this week</span>
                  </div>
                </div>

                <div className="status-card issues">
                  <div className="card-icon">⚠️</div>
                  <h3>Needs Attention</h3>
                  <div className="status-value">
                    {labStatus.totalComputers - labStatus.workingComputers}
                  </div>
                  <div className="status-trend">
                    <span className="trend-down">-1 from yesterday</span>
                  </div>
                </div>

                <div className="status-card availability">
                  <div className="card-icon">📊</div>
                  <h3>Availability</h3>
                  <div className="status-value">
                    {labStatus.totalComputers > 0 
                      ? Math.round((labStatus.workingComputers / labStatus.totalComputers) * 100)
                      : 0}%
                  </div>
                  <div className="status-trend">
                    <span className="trend-up">+5% this month</span>
                  </div>
                </div>
              </div>

              <div className="update-section">
                <h3>Update Lab Status</h3>
                <form onSubmit={handleSubmit} className="update-form">
                  <div className="form-group">
                    <label htmlFor="totalComputers">Total Computers:</label>
                    <input
                      type="number"
                      id="totalComputers"
                      name="totalComputers"
                      min="0"
                      value={formData.totalComputers}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="workingComputers">Working Computers:</label>
                    <input
                      type="number"
                      id="workingComputers"
                      name="workingComputers"
                      min="0"
                      max={formData.totalComputers || ''}
                      value={formData.workingComputers}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <button 
                    type="submit" 
                    className="update-btn" 
                    disabled={isUpdating}
                  >
                    {isUpdating ? (
                      <>
                        <span className="spinner"></span> Updating...
                      </>
                    ) : (
                      'Update Status'
                    )}
                  </button>
                </form>

                {success && (
                  <div className="success-message">
                    <span className="success-icon">✓</span>
                    {success}
                  </div>
                )}
              </div>
            </div>

            <div className="computer-list-section">
              <h2>Manage Computers</h2>
              <ComputerList onStatusChange={handleComputerStatusChange} />
            </div>
          </>
        )}

        {labStatus.lastUpdated && (
          <div className="last-updated">
            Last updated: {new Date(labStatus.lastUpdated).toLocaleString()}
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
