import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [counts, setCounts] = useState({
    working: 0,
    notWorking: 0
  });

  // Simulate data fetching
  useEffect(() => {
    const fetchCounts = async () => {
      try {
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 800));

        // TODO: Replace with actual API calls
        // const workingRes = await fetch('/api/computers/working/count');
        // const notWorkingRes = await fetch('/api/computers/not-working/count');
        // const workingData = await workingRes.json();
        // const notWorkingData = await notWorkingRes.json();

        // Mock data
        setCounts({
          working: 24, // workingData.count
          notWorking: 3 // notWorkingData.count
        });
      } catch (error) {
        console.error('Error fetching computer counts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCounts();
  }, []);

  const handleCardClick = (path) => {
    // Add a slight delay for better UX
    setTimeout(() => {
      navigate(path);
    }, 150);
  };

  return (
    <div className="home-container">
      <h1>Lab Management System</h1>
      <div className="cards-container">
        <div 
          className="status-card working"
          onClick={() => handleCardClick('/working')}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => e.key === 'Enter' && handleCardClick('/working')}
          aria-label="View working computers"
        >
          <h2>Working Computers</h2>
          <div className="status-count">
            {loading ? (
              <div className="loading-dots">
                <span>.</span>
                <span>.</span>
                <span>.</span>
              </div>
            ) : (
              counts.working
            )}
          </div>
        </div>

        <div 
          className="status-card not-working"
          onClick={() => handleCardClick('/non-working')}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => e.key === 'Enter' && handleCardClick('/non-working')}
          aria-label="View non-working computers"
        >
          <h2>Non-Working Computers</h2>
          <div className="status-count">
            {loading ? (
              <div className="loading-dots">
                <span>.</span>
                <span>.</span>
                <span>.</span>
              </div>
            ) : (
              counts.notWorking
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
